mapper.select:
SELECT SIDO_CD ,SGGEMD_CD ,SIGUNGU_NM ,EUPMYEONDONG_NM FROM ST_ADDR_DEPT2 WHERE 1=1 AND SIDO_CD = #{sidoCd}
AND SIGUNGU_NM = #{sigunguNm}
AND EUPMYEONDONG_NM LIKE CONCAT('%', #{eupmyeondongNm}, '%')
ORDER BY EUPMYEONDONG_NM ASC LIMIT 1

